import axios from 'axios';

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5000', // Your backend server URL
  headers: {
    'Content-Type': 'application/json',
  },
});

// Fetch products from the backend
export const fetchProducts = async () => {
  try {
    const response = await axiosInstance.get('/products');
    return response.data;
  } catch (error) {
    console.error('Error fetching products:', error);
    throw error;
  }
};

// Register a new user
export const registerUser = async (userData) => {
  try {
    const response = await axiosInstance.post('/auth/register', userData); // ✅ Fixed Endpoint
    return response.data;
  } catch (error) {
    console.error('Error registering user:', error);
    throw error;
  }
};

// Login user and return token
export const loginUser = async (loginData) => {
  try {
    const response = await axiosInstance.post('/auth/login', loginData);

    // Store the token in localStorage if login is successful
    const token = response.data.token; // Assuming token is returned in response data
    if (token) {
      localStorage.setItem('authToken', token); // Store token in localStorage
    }

    return response.data; // You can return any additional data, like the user data
  } catch (error) {
    console.error('Error logging in:', error);
    throw error;
  }
};

// Logout user
export const logoutUser = () => {
  localStorage.removeItem('authToken'); // Remove token from localStorage
};

export default axiosInstance;
