import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AppBar, Toolbar, Button, Typography, Box, Snackbar, Alert } from '@mui/material';

const Header = () => {
  const navigate = useNavigate();
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [logoutMessage, setLogoutMessage] = useState(false);

  // Function to check if the user is authenticated
  const checkAuthStatus = () => {
    const token = localStorage.getItem('authToken'); // Check for token in localStorage
    setIsAuthenticated(!!token); // If token exists, user is authenticated
  };

  // Effect hook to check authentication status
  useEffect(() => {
    checkAuthStatus(); // Check auth status when component mounts

    // Listen for changes to localStorage (for example, after login or logout)
    const handleStorageChange = () => {
      checkAuthStatus(); // Re-check authentication status
    };

    window.addEventListener('storage', handleStorageChange); // Listen to changes in localStorage

    return () => {
      window.removeEventListener('storage', handleStorageChange); // Cleanup on component unmount
    };
  }, []);

  // Handle logout functionality
  const handleLogout = () => {
    localStorage.removeItem('authToken'); // Remove token from localStorage
    setIsAuthenticated(false); // Update the state to reflect the user is logged out
    setLogoutMessage(true); // Show logout success message
    setTimeout(() => {
      navigate('/login'); // Redirect to login after 2 seconds
    }, 2000);
  };

  return (
    <>
      <AppBar position="sticky">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Plant E-Commerce
          </Typography>
          <Box>
            <Button color="inherit" component={Link} to="/">
              Home
            </Button>
            <Button color="inherit" component={Link} to="/products">
              Products
            </Button>
            <Button color="inherit" component={Link} to="/cart">
              Cart
            </Button>

            {/* Conditional rendering for buttons based on authentication status */}
            {!isAuthenticated ? (
              <>
                <Button color="inherit" component={Link} to="/register">
                  Register
                </Button>
                <Button color="inherit" component={Link} to="/login">
                  Login
                </Button>
              </>
            ) : (
              <Button color="inherit" onClick={handleLogout}>
                Logout
              </Button>
            )}
          </Box>
        </Toolbar>
      </AppBar>

      {/* Snackbar to show logout success message */}
      <Snackbar open={logoutMessage} autoHideDuration={2000} onClose={() => setLogoutMessage(false)}>
        <Alert onClose={() => setLogoutMessage(false)} severity="success" variant="filled">
          Logout successful!
        </Alert>
      </Snackbar>
    </>
  );
};

export default Header;
